import React, { useState, useRef, useEffect } from "react";
import { 
  Send, Bot, User, TrendingUp, DollarSign, BarChart3, Sparkles, 
  Home, MessageSquare, Settings, LogOut, Menu, X, Plus, Search,
  Clock, Star, Trash2, Edit3, ArrowRight, PieChart, LineChart,
  Building2, Globe, Shield, Users
} from "lucide-react";

export default function FinTastic() {
  // Auth state
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showAuth, setShowAuth] = useState(false);
  const [authMode, setAuthMode] = useState('login'); // 'login' or 'signup'

  // App state
  const [currentView, setCurrentView] = useState('home'); // 'home', 'chat'
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [chatHistory, setChatHistory] = useState([]);
  const [currentChatId, setCurrentChatId] = useState(null);

  // Chat state
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  // Load user data on mount
  useEffect(() => {
    const savedUser = JSON.parse(localStorage.getItem('fintastic_user') || 'null');
    const savedHistory = JSON.parse(localStorage.getItem('fintastic_history') || '[]');

    if (savedUser) {
      setUser(savedUser);
      setIsAuthenticated(true);
      setChatHistory(savedHistory);
    }
  }, []);

  // Auth functions
  const handleAuth = (email, password, username = '') => {
    if (authMode === 'signup') {
      const newUser = {
        id: Date.now(),
        email,
        username: username || email.split('@')[0],
        createdAt: new Date().toISOString()
      };
      localStorage.setItem('fintastic_user', JSON.stringify(newUser));
      setUser(newUser);
    } else {
      // For demo purposes, accept any email/password
      const existingUser = {
        id: Date.now(),
        email,
        username: email.split('@')[0],
        createdAt: new Date().toISOString()
      };
      setUser(existingUser);
    }
    setIsAuthenticated(true);
    setShowAuth(false);
  };

  const handleLogout = () => {
    localStorage.removeItem('fintastic_user');
    localStorage.removeItem('fintastic_history');
    setUser(null);
    setIsAuthenticated(false);
    setChatHistory([]);
    setCurrentView('home');
  };

  // Chat functions
  const startNewChat = () => {
    const newChatId = Date.now();
    const initialMessage = {
      id: 1,
      type: 'bot',
      content: "ðŸ‘‹ Hello! I'm FinTastic, your AI-powered financial research assistant. I can help you analyze company performance, compare financials, and provide insights on market trends. What would you like to know?",
      timestamp: new Date()
    };

    setCurrentChatId(newChatId);
    setMessages([initialMessage]);
    setCurrentView('chat');
    setSidebarOpen(false);

    const newChat = {
      id: newChatId,
      title: 'New Financial Analysis',
      messages: [initialMessage],
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString()
    };

    const updatedHistory = [newChat, ...chatHistory];
    setChatHistory(updatedHistory);
    localStorage.setItem('fintastic_history', JSON.stringify(updatedHistory));
  };

  const loadChat = (chatId) => {
    const chat = chatHistory.find(c => c.id === chatId);
    if (chat) {
      setCurrentChatId(chatId);
      setMessages(chat.messages);
      setCurrentView('chat');
      setSidebarOpen(false);
    }
  };

  const deleteChat = (chatId) => {
    const updatedHistory = chatHistory.filter(c => c.id !== chatId);
    setChatHistory(updatedHistory);
    localStorage.setItem('fintastic_history', JSON.stringify(updatedHistory));

    if (currentChatId === chatId) {
      setCurrentView('home');
      setCurrentChatId(null);
      setMessages([]);
    }
  };

  const updateChatTitle = (chatId, newTitle) => {
    const updatedHistory = chatHistory.map(chat => 
      chat.id === chatId ? { ...chat, title: newTitle } : chat
    );
    setChatHistory(updatedHistory);
    localStorage.setItem('fintastic_history', JSON.stringify(updatedHistory));
  };

  const scrollToBottom = () => {
    setTimeout(() => {
      messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
    }, 100);
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages, loading]); // Added loading as dependency

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim() || loading) return;

    const question = input.trim();

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: question,
      timestamp: new Date()
    };

    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput("");
    setLoading(true);

    if (messages.length === 1 && currentChatId) {
      const title = question.length > 50 ? question.substring(0, 50) + '...' : question;
      updateChatTitle(currentChatId, title);
    }

    try {
      const response = await fetch('/api/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question }),
      });

      if (!response.ok) {
        throw new Error(`API error: ${response.statusText}`);
      }

      const data = await response.json();

      const botMessage = {
        id: Date.now() + 1,
        type: 'bot',
        content: data.answer || "Sorry, I couldn't find an answer.",
        timestamp: new Date()
      };

      const finalMessages = [...updatedMessages, botMessage];
      setMessages(finalMessages);

      if (currentChatId) {
        const updatedHistory = chatHistory.map(chat => 
          chat.id === currentChatId 
            ? { ...chat, messages: finalMessages, updatedAt: new Date().toISOString() }
            : chat
        );
        setChatHistory(updatedHistory);
        localStorage.setItem('fintastic_history', JSON.stringify(updatedHistory));
      }
    } catch (error) {
      console.error("Error fetching answer:", error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'bot',
        content: "I'm experiencing some technical difficulties. Please check your connection and try again.",
        timestamp: new Date()
      };
      setMessages([...updatedMessages, errorMessage]);
    } finally {
      setLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  };

  // Auth Component
  const AuthModal = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [username, setUsername] = useState('');

    return (
      <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50 p-4">
        <div className="bg-white rounded-2xl p-8 w-full max-w-md">
          <div className="text-center mb-6">
            <div className="w-16 h-16 bg-gradient-to-r from-green-600 to-green-700 rounded-xl flex items-center justify-center mx-auto mb-4">
              <TrendingUp className="w-8 h-8 text-white" />
            </div>
            <h2 className="text-2xl font-bold text-gray-800">
              {authMode === 'login' ? 'Welcome Back' : 'Join FinTastic'}
            </h2>
            <p className="text-gray-600 mt-2">
              {authMode === 'login' ? 'Sign in to access your financial insights' : 'Create your free account'}
            </p>
          </div>

          <form onSubmit={(e) => {
            e.preventDefault();
            handleAuth(email, password, username);
          }} className="space-y-4">
            {authMode === 'signup' && (
              <input
                type="text"
                placeholder="Username (optional)"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500"
              />
            )}
            <input
              type="email"
              placeholder="Email address"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500"
            />
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              className="w-full px-4 py-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500"
            />
            <button
              type="submit"
              className="w-full bg-green-600 text-white py-3 rounded-xl hover:bg-green-700 transition-colors font-semibold"
            >
              {authMode === 'login' ? 'Sign In' : 'Create Account'}
            </button>
          </form>

          <div className="mt-6 text-center">
            <button
              onClick={() => setAuthMode(authMode === 'login' ? 'signup' : 'login')}
              className="text-green-600 hover:text-green-700"
            >
              {authMode === 'login' ? "Don't have an account? Sign up" : "Already have an account? Sign in"}
            </button>
          </div>

          <button
            onClick={() => setShowAuth(false)}
            className="absolute top-4 right-4 text-gray-400 hover:text-gray-600"
          >
            <X className="w-6 h-6" />
          </button>
        </div>
      </div>
    );
  };

  // Home Page Component
  const HomePage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-green-50">
      {/* Hero Section */}
      <section className="relative py-20 px-4">
        <div className="max-w-6xl mx-auto text-center">
          <div className="w-20 h-20 bg-gradient-to-r from-green-600 to-green-700 rounded-2xl flex items-center justify-center mx-auto mb-8">
            <TrendingUp className="w-10 h-10 text-white" />
          </div>
          <h1 className="text-5xl md:text-6xl font-bold bg-gradient-to-r from-green-700 to-green-800 bg-clip-text text-transparent mb-6">
            FinTastic
          </h1>
          <p className="text-xl md:text-2xl text-gray-600 mb-8 max-w-3xl mx-auto">
            AI-powered financial research assistant that transforms complex market data into actionable insights
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button
              onClick={isAuthenticated ? startNewChat : () => setShowAuth(true)}
              className="bg-green-600 text-white px-8 py-4 rounded-xl hover:bg-green-700 transition-all duration-200 font-semibold flex items-center justify-center space-x-2"
            >
              <MessageSquare className="w-5 h-5" />
              <span>Start Financial Analysis</span>
              <ArrowRight className="w-5 h-5" />
            </button>
            {!isAuthenticated && (
              <button
                onClick={() => setShowAuth(true)}
                className="border-2 border-green-600 text-green-600 px-8 py-4 rounded-xl hover:bg-green-50 transition-all duration-200 font-semibold"
              >
                Sign Up Free
              </button>
            )}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-20 px-4">
        <div className="max-w-6xl mx-auto">
          <h2 className="text-3xl md:text-4xl font-bold text-center text-gray-800 mb-16">
            Powerful Financial Intelligence
          </h2>
