import React, { useState, useRef, useEffect, useCallback } from "react";
import { Send, Bot, User, Menu } from "lucide-react";

const ChatInterface = ({ 
  messages, 
  setMessages, 
  chatHistory, 
  setChatHistory,
  currentChatId,
  setCurrentChatId,
  onToggleSidebar,
  updateChatTitle 
}) => {
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const textareaRef = useRef(null);

  const suggestedQuestions = [
    "What are Apple's recent financial highlights?",
    "Compare Tesla vs Ford's performance",
    "Show me Microsoft's revenue trends",
    "What's Amazon's EBITA in the past years?"
  ];

  const scrollToBottom = useCallback(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, []);

  useEffect(() => {
    if (messages.length > 0) {
      scrollToBottom();
    }
  }, [messages.length, scrollToBottom]);

  const handleSuggestionClick = useCallback((question) => {
    setInput(question);
    textareaRef.current?.focus();
  }, []);

  const handleSubmit = useCallback(async (e) => {
    e.preventDefault();
    if (!input.trim() || loading) return;

    const question = input.trim();

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: question,
      timestamp: new Date()
    };

    const updatedMessages = [...messages, userMessage];
    setMessages(updatedMessages);
    setInput("");
    setLoading(true);

    // Update chat title if this is the first user message
    if (messages.length === 1 && currentChatId) {
      const title = question.length > 50 ? question.substring(0, 50) + '...' : question;
      updateChatTitle(currentChatId, title);
    }

    try {
      const response = await fetch('/api/ask', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question }),
      });

      if (!response.ok) {
        throw new Error(`API error: ${response.statusText}`);
      }

      const data = await response.json();

      const botMessage = {
        id: Date.now() + 1,
        type: 'bot',
        content: data.answer || "Sorry, I couldn't find an answer.",
        timestamp: new Date()
      };

      const finalMessages = [...updatedMessages, botMessage];
      setMessages(finalMessages);

      // Update chat history
      if (currentChatId) {
        const updatedHistory = chatHistory.map(chat => 
          chat.id === currentChatId 
            ? { ...chat, messages: finalMessages, updatedAt: new Date().toISOString() }
            : chat
        );
        setChatHistory(updatedHistory);
        localStorage.setItem('fintastic_history', JSON.stringify(updatedHistory));
      }
    } catch (error) {
      console.error("Error fetching answer:", error);
      const errorMessage = {
        id: Date.now() + 1,
        type: 'bot',
        content: "I'm experiencing some technical difficulties. Please check your connection and try again.",
        timestamp: new Date()
      };
      setMessages([...updatedMessages, errorMessage]);
    } finally {
      setLoading(false);
    }
  }, [input, loading, messages, currentChatId, chatHistory, setMessages, setChatHistory, updateChatTitle]);

  const handleKeyPress = useCallback((e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
  }, [handleSubmit]);

  const handleInputChange = useCallback((e) => {
    setInput(e.target.value);
  }, []);

  return (
    <div className="flex-1 flex flex-col h-screen">
      {/* Chat Header */}
      <div className="bg-white/80 backdrop-blur-sm border-b border-green-100 p-4 flex-shrink-0">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-3">
            <button
              onClick={onToggleSidebar}
              className="lg:hidden text-gray-500 hover:text-gray-700"
            >
              <Menu className="w-6 h-6" />
            </button>
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-gradient-to-r from-green-600 to-green-700 rounded-lg flex items-center justify-center">
                <Bot className="w-5 h-5 text-white" />
              </div>
              <div>
                <h2 className="font-semibold text-gray-800">AI Financial Assistant</h2>
                <p className="text-sm text-green-600">Ready to analyze</p>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Messages Container */}
      <div className="flex-1 overflow-y-auto bg-gradient-to-br from-slate-50 to-green-50">
        <div className="max-w-4xl mx-auto px-4 py-6">
          {messages.map((message) => (
            <div
              key={message.id}
              className={`flex mb-6 ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div className={`flex max-w-[80%] ${message.type === 'user' ? 'flex-row-reverse' : 'flex-row'}`}>
                <div className={`flex-shrink-0 ${message.type === 'user' ? 'ml-3' : 'mr-3'}`}>
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center ${
                    message.type === 'user' 
                      ? 'bg-green-600' 
                      : 'bg-gradient-to-r from-green-500 to-green-600'
                  }`}>
                    {message.type === 'user' ? 
                      <User className="w-5 h-5 text-white" /> : 
                      <Bot className="w-5 h-5 text-white" />
                    }
                  </div>
                </div>

                <div className={`rounded-2xl px-4 py-3 ${
                  message.type === 'user'
                    ? 'bg-green-600 text-white'
                    : 'bg-white border border-green-100 text-gray-800'
                }`}>
                  <div className="whitespace-pre-wrap leading-relaxed">
                    {message.content}
                  </div>
                  <div className={`text-xs mt-2 ${
                    message.type === 'user' ? 'text-green-100' : 'text-gray-500'
                  }`}>
                    {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                  </div>
                </div>
              </div>
            </div>
          ))}

          {loading && (
            <div className="flex justify-start mb-6">
              <div className="flex max-w-[80%]">
                <div className="mr-3">
                  <div className="w-8 h-8 rounded-full bg-gradient-to-r from-green-500 to-green-600 flex items-center justify-center">
                    <Bot className="w-5 h-5 text-white" />
                  </div>
                </div>
                <div className="bg-white border border-green-100 rounded-2xl px-4 py-3">
                  <div className="flex items-center space-x-2">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.1s'}}></div>
                      <div className="w-2 h-2 bg-green-500 rounded-full animate-bounce" style={{animationDelay: '0.2s'}}></div>
                    </div>
                    <span className="text-green-600 text-sm">Analyzing...</span>
                  </div>
                </div>
              </div>
            </div>
          )}

          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* Suggested Questions */}
      {messages.length === 1 && (
        <div className="max-w-4xl mx-auto px-4 pb-4 flex-shrink-0">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
            {suggestedQuestions.map((question, index) => (
              <button
                key={index}
                onClick={() => handleSuggestionClick(question)}
                className="text-left p-3 bg-white border border-green-200 rounded-xl hover:border-green-300 hover:bg-green-50 transition-all duration-200 text-sm text-gray-700"
              >
                {question}
              </button>
            ))}
          </div>
        </div>
      )}

<div className="bg-white/80 backdrop-blur-sm border-t border-green-100">
        <div className="max-w-4xl mx-auto px-4 py-4">
          <div className="relative">
            <div className="flex items-end space-x-3">
              <div className="flex-1 relative">
                <textarea
                  ref={textareaRef}
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyDown={handleKeyPress}
                  placeholder="Ask about company financials, market trends, or investment insights..."
                  className="w-full resize-none rounded-xl border border-green-200 px-4 py-3 pr-12 focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-transparent placeholder-gray-500 text-gray-800"
                  rows="1"
                  style={{
                    minHeight: '48px',
                    maxHeight: '120px',
                    overflowY: 'auto'
                  }}
                />
                <button
                  onClick={handleSubmit}
                  disabled={!input.trim() || loading}
                  className={`absolute right-2 bottom-2 p-2 rounded-lg transition-all duration-200 ${
                    input.trim() && !loading
                      ? 'bg-green-600 hover:bg-green-700 text-white shadow-sm'
                      : 'bg-gray-200 text-gray-400 cursor-not-allowed'
                  }`}
                >
                  <Send className="w-4 h-4" />
                </button>
              </div>
            </div>
          </div>

          <div className="mt-2 text-xs text-gray-500 text-center">
            FinTastic can make mistakes. Consider checking important financial information.
          </div>
        </div>
      </div>
    </div>
  );
}
